import React from "react";
import firebase from "firebase";
import Nav from "../nav/Nav";
import { JokeInput } from "./JokeInput";
import trashIcon from "./bin.png";
import "./User.css";

function Jokes() {
  const [jokes, setJokes] = React.useState([]);
  const [newJokeContent, setNewJokeContent] = React.useState("");
  const user = localStorage.user;

  React.useEffect(() => {
    const db = firebase.firestore();
    return db
      .collection("users")
      .doc(user)
      .collection("savedjokes")
      .onSnapshot(snapshot => {
        const jokesData = [];
        snapshot.forEach(doc => jokesData.push({ ...doc.data(), id: doc.id }));
        setJokes(jokesData);
      });
  }, [user]);

  const onCreate = () => {
    const db = firebase.firestore();
    db.collection("users")
      .doc(user)
      .collection("savedjokes")
      .add({ content: newJokeContent });
  };

  const myStyle = {
    flexDirection: "column"
  };

  return (
    <div>
      <Nav />
      <input
        value={newJokeContent}
        onChange={e => setNewJokeContent(e.target.value)}
      />
      <button onClick={onCreate}>Add</button>
      <ul>
        {jokes.map(joke => (
          <li style={myStyle} key={joke.id}>
            <JokeInput joke={joke} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Jokes;

