import React from "react";
import firebase from "firebase";
import { ListGroup } from "react-bootstrap";

const jokeInputStyle = {
  textAlign: "left",
  flex: "none",
  width: "1000px"
};

const buttonStyle = {
  textAlign: "right"
};

export const JokeInput = ({ joke }) => {
  const [content, setContent] = React.useState(joke.content);
  const user = localStorage.user;
  /*   const onUpdate = () => {
    const db = fire.firestore();
    db.collection("users")
      .doc(user)
      .collection("savedjokes")
      .add({ ...joke, content });
  }; */

  const onDelete = () => {
    const db = firebase.firestore();
    db.collection("users")
      .doc(user)
      .collection("savedjokes")
      .doc(joke.id)
      .delete();
  };

  return (
    <div style={jokeInputStyle}>
      <div>
        <ListGroup.Item>
          {content}
          <a href="https://icons8.com/icon/102315/remove" onClick={onDelete} />
        </ListGroup.Item>
      </div>
    </div>
  );
};
