import React from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>this is App</p>
        <h1>
          <FetchRandom />
        </h1>
      </header>
    </div>
  );
}

class FetchRandom extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      setup: "",
      punchline: "",
      onClick: false
    };
    this.displayPunch = this.displayPunch.bind(this);
  }

  async componentDidMount() {
    const url =
      "https://us-central1-dadsofunny.cloudfunctions.net/DadJokes/random/jokes";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({
      loading: false,
      setup: data.setup,
      punchline: data.punchline
    });
    console.log(data);
  }

  displayPunch() {
    this.setState({
      onClick: !this.state.onClick
    });
  }

  render() {
    return (
      <div>
        {this.state.loading ? (
          <div>loading...</div>
        ) : (
          <div>
            <div>{this.state.setup}</div>
            <button onClick={this.displayPunch}>
              {this.state.onClick ? "Hide answer " : "Show answer"}
            </button>
            {this.state.onClick ? <div>{this.state.punchline}</div> : <></>}
          </div>
        )}
      </div>
    );
  }
}

export default App;
