import React from "react";
import firebase from "firebase";

const jokeInputStyle = {
  textAlign: "left",
  width: "100%"
};

const buttonStyle = {
  textAlign: "right"
};

export const JokeInput = ({ joke }) => {
  const [content, setContent] = React.useState(joke.content);
  const user = localStorage.user;
  /*   const onUpdate = () => {
    const db = fire.firestore();
    db.collection("users")
      .doc(user)
      .collection("savedjokes")
      .add({ ...joke, content });
  }; */

  const onDelete = () => {
    const db = firebase.firestore();
    db.collection("users")
      .doc(user)
      .collection("savedjokes")
      .doc(joke.id)
      .delete();
  };

  return (
    <div style={jokeInputStyle}>
      <div>
        <h5>
          {content}{" "}
          <button style={buttonStyle} onClick={onDelete}>
            Delete
          </button>
        </h5>
      </div>
    </div>
  );
};
